import * as React from "react";
import { PropertyControls, ControlType } from "framer";

interface Props {
    browser: string;
    homeBar: string;
};

export class BrowserBottom extends React.Component<Props> {

    static defaultProps = {
        browser: "safari",
        height: 82,
        homeBar: "show",
        width: 375,
    }

    static propertyControls: PropertyControls = {
        browser: {
            type: ControlType.Enum,
            options: ["safari", "chrome"],
            optionTitles: ["Safari", "Chrome"],
            title: "Browser"
        },
        homeBar: {
            type: ControlType.SegmentedEnum,
            options: ["show", "hide"],
            optionTitles: ["Show", "Hide"],
            title: "Home Bar"
        },
    }

    render() {
        const { homeBar, browser } = this.props;

        return (
            <div
                style={{
                    background: browser === "safari" ? "#F9F9F9" : "#F8F8F8",
                    boxShadow: browser === "safari" ? "0px -0.5px #B2B2B2" : "none",
                    color: "#414141",
                    display: "flex",
                    fontWeight: "normal",
                    fontSize: 17,
                    height: 82,
                    overflow: "hidden",
                    position: "relative",
                    width: "100%",
                }}
            >
                <div 
                    style={{
                        alignItems: "baseline",
                        background: "transparent",
                        display: "flex",
                        height: "38",
                        justifyContent: "space-between",
                        marginTop: browser === "safari" ? 11 : 5,
                        marginLeft: browser === "safari" ? 9 : 13,
                        marginRight: browser === "safari" ? 16 : 13,
                        position: "relative",
                        width: "100%",
                    }} 
                >

                    <div
                        dangerouslySetInnerHTML={{ 
                            __html: browser === "safari" ? aleft_safari() : aleft_chrome()
                        }}
                    />
                    
                    <div
                        style={{ 
                            left: browser === "safari" ? 3 : 4,
                            position: "relative",
                        }} 
                        dangerouslySetInnerHTML={{ 
                            __html: browser === "safari" ? aright_safari() : aright_chrome()
                        }}
                    />

                    <div 
                        style={{
                            background: "#E1E1E1", 
                            borderRadius: 19,
                            display: browser === "safari" ? "none" : "block",
                            height: 38,
                            width: 70,
                        }}
                    >
                        <div
                            style={{ 
                                margin: "auto",
                                marginTop: 7,
                                paddingRight: 3,
                                textAlign: "center",
                            }}
                            dangerouslySetInnerHTML={{ 
                                __html: search_chrome() 
                            }}
                        />
                    </div>

                    <div
                        style={{ 
                            left: 3,
                            display: browser === "safari" ? "block" : "none",
                            position: "relative",
                        }} 
                        dangerouslySetInnerHTML={{ 
                            __html: share_safari()
                        }}
                    />

                    <div 
                        style={{ 
                            background: "transparent",
                            right: browser === "safari" ? -2 : 5,
                            position: "relative",
                        }} 
                    >
                        <div
                            style={{ 
                                margin: "auto",
                                left: 0,
                                right: 0,
                                position: "relative",
                                textAlign: "center",
                            }}
                            dangerouslySetInnerHTML={{
                                __html: browser === "safari" ? bookmark_safari() : page_chrome()
                            }}
                        />
                        <div 
                            style={{
                                color: "#7B7B7B",
                                display: browser === "safari" ? "none" : "block",
                                fontFamily: "SF-Pro-Text, system-ui, -apple-system",
                                fontWeight: "bold",
                                fontSize: 13,
                                left: 10,
                                position: "absolute",
                                top: 4,
                            }} 
                        >
                        1
                        </div>
                    </div>

                    <div
                        dangerouslySetInnerHTML={{ 
                            __html: browser === "safari" ? page_safari() : more_chrome()
                        }}
                    />

                </div>

                <div
                    style={{
                        background: "#000000",
                        borderRadius: 100,
                        bottom: 8,
                        display: homeBar === "show" ? "block" : "none",
                        height: 5,
                        left:0,
                        margin: "0 auto",
                        position: "absolute",
                        right: 0,
                        width: 133,
                    }}
                />
                
            </div>
        );
    }
}

