import * as React from "react";
import { PropertyControls, ControlType } from "framer";
import { string } from "prop-types";

interface Props {
    browser: string;
    statusBar: string;
    text: string;
};

export class BrowserTop extends React.Component<Props> {

    static defaultProps = {
        browser: "safari",
        height: 94,
        statusBar: "show",
        text: "yourwebsite.com",
        width: 375,
    }
    
    static propertyControls: PropertyControls = {
        browser: {
            type: ControlType.Enum,
            options: ["safari", "chrome"],
            optionTitles: ["Safari", "Chrome"],
            title: "Browser"
        },
        statusBar: {
            type: ControlType.SegmentedEnum,
            options: ["show", "hide"],
            optionTitles: ["Show", "Hide"],
            title: "Status Bar"
        },
        text: {
            type: ControlType.String, title: "URL"
        }
    }
    
    render() {
        const { statusBar, browser } = this.props;
        return (
            <div
                style={{
                    background: browser === "safari" ? "#F9F9F9" : "#F4F4F5",
                    boxShadow: browser === "safari" ? "0px 0.5px #B2B2B2" : "none",
                    color: "#414141",
                    display: "flex",
                    fontFamily: "SF-Pro-Text, system-ui, -apple-system",
                    fontWeight: browser === "safari" ? "500" : "normal",
                    fontSize: 17,
                    height: browser === "safari" ? 94 : 92,
                    overflow: "hidden",
                    position: "relative",
                    width: "100%",
                }}
            >
                <div
                    style={{
                        display: statusBar === "show" ? "block" : "none",
                    }}
                >
                
                    <div
                        style={{
                            alignItems: "center",
                            color: "#000000",
                            display:"flex",
                            fontSize: "15px",
                            fontFamily: "SF-Pro-Text, system-ui, -apple-system",
                            fontWeight: "600",
                            height: 20,
                            justifyContent: "center",
                            left: 14,
                            lineHeight: "20px",
                            padding: 0,
                            position: "absolute",
                            top: 12,
                            width: 68,
                        }}
                    >
                        <div>
                            9:41
                        </div>
                        <div
                            style={{
                                height: 20
                            }}
                            dangerouslySetInnerHTML={{
                                __html: location()
                            }}
                        />
                    </div>

                    <div
                    style={{ 
                        display:"flex",
                        padding: 0,
                        position: "absolute",
                        right: 14,
                        top: 12,
                    }}
                >
                    <div
                        dangerouslySetInnerHTML={{ 
                            __html: signal() 
                        }}
                    />
                    <div 
                        style={{ 
                            marginLeft: 5
                        }}
                        dangerouslySetInnerHTML={{ 
                            __html: wifi() 
                        }}
                    />
                    <div 
                        style={{ 
                            marginLeft: 5 
                        }}
                        dangerouslySetInnerHTML={{ 
                            __html: battery() 
                        }}
                    />
                </div>
                
                </div>
                <div 
                    style={{
                        alignItems: "center",
                        background: browser === "safari" ? "#E7E8E9" : "#DBDBDC",
                        borderRadius: browser === "safari" ? 10 : 18,
                        bottom: browser === "safari" ? 10 : 6,
                        color: browser === "safari" ? "#000000" : "#414141",
                        fontFamily: browser === "safari" ? "HelveticaNeue, system-ui, -apple-system" : "SF-Pro-Text, system-ui, -apple-system",
                        display: "flex",
                        height: 36,
                        justifyContent: "center",
                        left: browser === "safari" ? 10 : 8,
                        position: "absolute",
                        right: browser === "safari" ? 10 : 8,
                    }}
                >
                    <div
                        style={{ 
                            float: "left",
                            marginLeft: 0,
                            marginRight: browser === "safari" ? 11 : 9,
                            marginTop: browser === "safari" ? 0 : 1,
                        }}
                        dangerouslySetInnerHTML={{ 
                            __html: browser === "safari" ? lock_safari() : lock_chrome()
                        }}
                    />
                    <div>
                        {this.props.text}
                    </div>
                    <div
                        style={{
                            position: "absolute",
                            right: browser === "safari" ? 8 : 14,
                            top: browser === "safari" ? 8 : 7,
                        }}
                        dangerouslySetInnerHTML={{
                            __html: browser === "safari" ? refresh() : share()
                        }}
                    />
                </div>
            </div>
        );
    }
}

function location() {
    return `<?xml version="1.0" encoding="UTF-8"?>
    <svg width="16px" height="20px" viewBox="0 0 16 20" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
        <g id="Location" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd" stroke-linejoin="round">
            <g id="location" transform="translate(3.000000, 6.000000)" stroke="#000000">
                <polygon id="Shape" points="5.86818182 9.54545455 10 0 0.454545455 4.13181818 5.86818182 4.13181818"></polygon>
            </g>
        </g>
    </svg>`
}

